import React, { useEffect, useState } from "react";
import "./FoodItems"; 

import { Link, useNavigate } from 'react-router-dom';
import ApiService from '../../service/ApiService';

const Query = () => {
  const [queries, setQueries] = useState([]);
  const isAdmin = ApiService.isAdmin();
  const navigate = useNavigate();

  // Fetching the list of queries
  useEffect(() => {
    fetch("http://localhost:4040/api/queries") 
      .then((response) => response.json())
      .then((data) => setQueries(data))
      .catch((error) => console.error("Error fetching queries:", error));
  }, []);

  // Handle the "Add New Query" button click
  const handleAddQueryClick = () => {
    if (ApiService.isAdmin()) {
      navigate('/admin/add-query'); 
    } else {
      alert('Login as Admin to add a new query.');
    }
  };

  return (
    <div className="query-container">
      <h2>Query List</h2>
      {isAdmin && <button onClick={handleAddQueryClick}>Add New Query</button>}
      <table className="query-table">
        <thead>
          <tr>
            <th>Query ID</th>
            <th>Sender Name</th>
            <th>Query</th>
            <th>Sender Email</th>
            <th>Reply</th>
          </tr>
        </thead>
        <tbody>
          {queries.length > 0 ? (
            queries.map((query) => (
              <tr key={query.id}>
                <td>{query.id}</td>
                <td>{query.senderName}</td>
                <td>{query.query}</td>
                <td>{query.senderEmail}</td>
                <td>{query.replyToQuery || "No reply yet"}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">No queries available.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Query;
