import React, { useEffect, useState } from 'react';
import './Blog.css';
import { Link } from 'react-router-dom';
import { Card } from 'react-bootstrap';


function Services() {
  const [services, setServices] = useState([]);


  useEffect(() => {
    fetch('http://localhost:4040/api/services')
      .then((response) => response.json())
      .then((data) => {
        console.log('Fetched services:', data); 
        setServices(data);
      })
      .catch((error) => console.error('Error fetching services:', error));
  }, []);


  return (
    <div
  className='services-page'
  style={{ backgroundImage: `url(/assets/images/res02.png)` }}
>
      <header className='height-75'>
        <div className='container h-100 d-flex flex-column align-items-center justify-content-center text-light'>
          <h1 className='text-center fw-semibold'>Our Services</h1>
          <p className='text-center w-75 mb-5'>
            Explore our range of exquisite services designed to offer you an unforgettable dining experience.
          </p>
        </div>
      </header>
      <div style={styles.servicesSection}>
        <h2 style={styles.sectionTitle}>Our Services</h2>
        <div style={styles.services}>
          <div style={styles.serviceCard}>
            <img src={serviceImage1} alt="Service 1" style={styles.serviceImage} />
            <h3 style={styles.serviceTitle}>Dine-In</h3>
            <p style={styles.serviceText}>Experience our exquisite dining experience in a luxurious environment.</p>
          </div>
          <div style={styles.serviceCard}>
            <img src={serviceImage2} alt="Service 2" style={styles.serviceImage} />
            <h3 style={styles.serviceTitle}>Delivery</h3>
            <p style={styles.serviceText}>Enjoy our gourmet meals delivered right to your doorstep.</p>
          </div>
          <div style={styles.serviceCard}>
            <img src={serviceImage3} alt="Service 3" style={styles.serviceImage} />
            <h3 style={styles.serviceTitle}>Event Catering</h3>
            <p style={styles.serviceText}>Let us cater your events with our exclusive dishes and services.</p>
          </div>
        </div>
      </div>

      <div className='bg-body-tertiary py-5'>
        <div className="container">
          <div className="row g-4">
            {services.map((service) => (
              <div key={service.id} className='col-md-6 col-lg-4'>
                <Link to={`/services/${service.id}`} className='text-decoration-none'>
                  <Card className='h-100 shadow scale-hover-effect bg-dark text-light border-0'>
                    <Card.Img variant="top" height='300px'  src={service.img} />
                    <Card.Body className='p-md-5 d-flex flex-column align-items-center'>
                      <Card.Title className='fs-2 mb-4'>{service.title}</Card.Title>
                      <Card.Text className='text-center'>{service.description}</Card.Text>
                    </Card.Body>
                  </Card>
                </Link>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    color: '#5D4037', // Earthy brown color for the text
    backgroundColor: '#F5F5DC', // Beige background for the entire page
  },
  heading: {
    fontFamily: "'Playfair Display', serif",
    fontSize: '4rem',
    color: '#8B4513', // Terracotta color for the heading
    marginBottom: '10px',
  },
  paragraph: {
    fontFamily: "'Open Sans', sans-serif",
    fontSize: '1.5rem',
    color: '#fff', //
    marginBottom: '20px',
    fontStyle: 'italic',
  },
  banner: {
    position: 'relative',
    width: '100%',
    height: '600px',
    overflow: 'hidden',
  },
  bannerImage: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',

    filter: 'brightness(60%)', // Slightly darker overlay to highlight the text
  },
  bannerText: {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    color: '#8B4513', // Terracotta color for the banner text
    textAlign: 'center',
  },
  exploreButton: {
    marginTop: '20px',
    padding: '10px 20px',
    backgroundColor: '#8B4513', // Terracotta color for the button background
    color: '#F5F5DC', // Beige color for the button text
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
  },
  servicesSection: {
    padding: '50px 20px',
    textAlign: 'center',
  },
  sectionTitle: {
    fontSize: '36px',
    color: '#8B4513', // Terracotta color for section titles
    marginBottom: '30px',
  },
  sectionText: {
    color: '#5D4037', // Earthy brown color for section text
  },
  services: {
    display: 'flex',
    justifyContent: 'space-around',
    flexWrap: 'wrap',
  },
  serviceCard: {
    width: '300px',
    margin: '20px',
    textAlign: 'center',
    boxShadow: '0px 4px 6px rgba(93, 64, 55, 0.5)', // Earthy brown shadow for the cards
    borderRadius: '10px',
    overflow: 'hidden',
    backgroundColor: '#D2B48C', // Light brown background for the service cards
  },
  serviceImage: {
    width: '100%',
    height: '200px',
    objectFit: 'cover',
  },
  serviceTitle: {
    color: '#8B4513', // Terracotta color for service titles
  },
  serviceText: {
    color: '#5D4037', // Earthy brown color for service descriptions
  },
  offersSection: {
    padding: '50px 20px',
    textAlign: 'center',
    backgroundColor: '#F5F5DC', // Beige background for the offers section
  },
  offersButton: {
    marginTop: '20px',
    padding: '10px 20px',
    backgroundColor: '#8B4513', // Terracotta color for the button background
    color: '#F5F5DC', // Beige color for the button text
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
  },
  gallerySection: {
    padding: '50px 20px',
    textAlign: 'center',
    backgroundColor: '#F5F5DC', // Beige background for the gallery section
  },
  galleryButton: {
    marginTop: '20px',
    padding: '10px 20px',
    backgroundColor: '#8B4513', // Terracotta color for the button background
    color: '#F5F5DC', // Beige color for the button text
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
  },
  contactSection: {
    padding: '50px 20px',
    textAlign: 'center',
    backgroundColor: '#D2B48C', // Light brown background for the contact section
    color: '#5D4037', // Earthy brown color for the contact section text
  },
  contactButton: {
    marginTop: '20px',
    padding: '10px 20px',
    backgroundColor: '#8B4513', // Terracotta color for the button background
    color: '#F5F5DC', // Beige color for the button text
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
  },

};


export default Services;
