// Home.js
import React from 'react';

const styles = {
  banner: {
    position: 'relative',
    height: '600px',
    overflow: 'hidden',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
  },
  bannerImage: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
    filter: 'brightness(60%)',
    transition: 'filter 0.3s ease-in-out, transform 0.3s ease-in-out',
    position: 'absolute',
    top: 0,
    left: 0,
  },
  bannerText: {
    position: 'relative',
    color: '#F5F5DC', // Beige color
    fontSize: '2.5rem',
    fontWeight: 'bold',
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.5)', // Shadow for readability
    transition: 'color 0.3s ease-in-out, transform 0.3s ease-in-out',
    zIndex: 1, // Ensure text is above image
  },
  bannerHover: {
    filter: 'brightness(70%)',
    transform: 'scale(1.05)',
  },
  bannerTextHover: {
    color: '#FFD700', // Gold color on hover
    transform: 'translateY(-10px)',
  },
  servicesSection: {
    padding: '50px 0',
    backgroundColor: '#F5F5F5', // Light gray background
    display: 'flex',
    flexWrap: 'wrap',
    gap: '20px',
    justifyContent: 'center',
  },
  serviceCard: {
    backgroundColor: '#D2B48C', // Light brown
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    transition: 'transform 0.3s ease, box-shadow 0.3s ease',
    overflow: 'hidden',
    width: '300px',
    textAlign: 'center',
    padding: '20px',
  },
  serviceCardHover: {
    transform: 'scale(1.05)',
    boxShadow: '0 8px 16px rgba(0, 0, 0, 0.3)',
  },
  textBrown: {
    color: '#8B4513', // Terracotta color
    transition: 'color 0.3s ease',
  },
  textBrownHover: {
    color: '#A0522D', // Darker terracotta on hover
  },
  bgBrown: {
    backgroundColor: '#D2B48C', // Light brown
    transition: 'background-color 0.3s ease',
  },
  bgBrownHover: {
    backgroundColor: '#C8A57B', // Slightly darker brown on hover
  },
};

const Home = () => {
  return (
    <div>
      {/* Banner Section */}
      <div
        style={styles.banner}
        onMouseEnter={(e) => {
          e.currentTarget.querySelector('.banner-image').style = styles.bannerHover;
          e.currentTarget.querySelector('.banner-text').style = styles.bannerTextHover;
        }}
        onMouseLeave={(e) => {
          e.currentTarget.querySelector('.banner-image').style = styles.bannerImage;
          e.currentTarget.querySelector('.banner-text').style = styles.bannerText;
        }}
      >
        <img src="banner-image.jpg" alt="Banner" className="banner-image" style={styles.bannerImage} />
        <div className="banner-text" style={styles.bannerText}>
          Welcome to Our Website
        </div>
      </div>

      {/* Services Section */}
      <div style={styles.servicesSection}>
        <div
          className="service-card"
          style={styles.serviceCard}
          onMouseEnter={(e) => e.currentTarget.style = styles.serviceCardHover}
          onMouseLeave={(e) => e.currentTarget.style = styles.serviceCard}
        >
          <h3 className="text-brown" style={styles.textBrown}>Service One</h3>
          <p>Detailed description of service one.</p>
        </div>
        <div
          className="service-card"
          style={styles.serviceCard}
          onMouseEnter={(e) => e.currentTarget.style = styles.serviceCardHover}
          onMouseLeave={(e) => e.currentTarget.style = styles.serviceCard}
        >
          <h3 className="text-brown" style={styles.textBrown}>Service Two</h3>
          <p>Detailed description of service two.</p>
        </div>
        <div
          className="service-card"
          style={styles.serviceCard}
          onMouseEnter={(e) => e.currentTarget.style = styles.serviceCardHover}
          onMouseLeave={(e) => e.currentTarget.style = styles.serviceCard}
        >
          <h3 className="text-brown" style={styles.textBrown}>Service Three</h3>
          <p>Detailed description of service three.</p>
        </div>
      </div>
    </div>
  );
};

export default Home;
