import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import ApiService from '../../service/ApiService';

const AdminPage = () => {
    const [adminName, setAdminName] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchAdminName = async () => {
            try {
                const response = await ApiService.getUserProfile();
                setAdminName(response.user.name);
            } catch (error) {
                console.error('Error fetching admin details:', error.message);
            }
        };

        fetchAdminName();
    }, []);

    return (
        <div className="admin-page">
            <h1 className="welcome-message">Welcome, {adminName}</h1>
            <div className="admin-actions">
                <button className="admin-button" onClick={() => navigate('/admin/manage-rooms')}>
                       Manage Rooms   
                </button>
                <button className="admin-button" onClick={() => navigate('/admin/manage-bookings')}>
                    Manage Bookings
                </button>
            </div>
            <div>
                <p></p>
            </div>
            <div className="admin-actions">
                <button className="admin-button" onClick={() => navigate('/services')}>
                     Manage Services
                </button>
                <button className="admin-button" onClick={() => navigate('/fooditems')}>
                    Manage FoodItems
                </button>
            </div>
            <div>
                <p></p>
            </div>
            <div className="admin-actions">
                <button className="admin-button" onClick={() => navigate('/admin/manage-rooms')}>
                     Manage Deliverys
                </button>
                <button className="admin-button" onClick={() => navigate('/admin/manage-bookings')}>
                    Manage Dine-in
                </button>
            </div>
            <div>
                <p></p>
            </div>
            <div className="admin-actions">
                <button className="admin-button" onClick={() => navigate('/query')}>
                     Manage Queries
                </button>
                <button className="admin-button" onClick={() => navigate('/viewusers')}>
                    Manage Users
                </button>
            </div>
        </div>
    );
}

export default AdminPage;
