import React, { useState, useEffect } from 'react';

const MakeReservation = () => {
  const [formData, setFormData] = useState({
    reservationType: 'dine-in', // default to dine-in
    recipientName: '',
    address: '',
    deliveryDate: '',
    inTime: '',
    outTime: '',
    foodItems: [],
    instructions: '',
  });

  const [availableFoodItems, setAvailableFoodItems] = useState([]);

  useEffect(() => {
    // Fetch food items from the database when component mounts
    const fetchFoodItems = async () => {
      // Simulate an API call to fetch food items
      const foodData = [
        { id: 1, name: 'Pizza' },
        { id: 2, name: 'Burger' },
        { id: 3, name: 'Pasta' },
      ];
      setAvailableFoodItems(foodData);
    };

    fetchFoodItems();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFoodSelection = (e) => {
    const options = e.target.options;
    const selectedItems = [];
    for (let i = 0, l = options.length; i < l; i++) {
      if (options[i].selected) {
        selectedItems.push(options[i].value);
      }
    }
    setFormData({ ...formData, foodItems: selectedItems });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Reservation Details Submitted:', formData);

    // Reset form after submission
    setFormData({
      reservationType: 'dine-in',
      recipientName: '',
      address: '',
      deliveryDate: '',
      inTime: '',
      outTime: '',
      foodItems: [],
      instructions: '',
    });
  };

  return (
    <div className="reservation-container">
      <h2>Make a Reservation</h2>
      <div className="reservation-form">
        <div className="form-group">
          <label>Reservation Type</label>
          <select name="reservationType" value={formData.reservationType} onChange={handleChange}>
            <option value="dine-in">Dine-In</option>
            <option value="delivery">Delivery</option>
          </select>
        </div>

        {formData.reservationType === 'delivery' && (
          <>
            <div className="form-group">
              <label>Recipient Name</label>
              <input
                type="text"
                name="recipientName"
                value={formData.recipientName}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Address</label>
              <input
                type="text"
                name="address"
                value={formData.address}
                onChange={handleChange}
                required
              />
            </div>
          </>
        )}

        <div className="form-group">
          <label>Reservation Date</label>
          <input
            type="date"
            name="deliveryDate"
            value={formData.deliveryDate}
            onChange={handleChange}
            required
          />
        </div>

        {formData.reservationType === 'dine-in' && (
          <>
            <div className="form-group">
              <label>In Time</label>
              <input
                type="time"
                name="inTime"
                value={formData.inTime}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Out Time</label>
              <input
                type="time"
                name="outTime"
                value={formData.outTime}
                onChange={handleChange}
              />
            </div>
          </>
        )}

        <div className="form-group">
          <label>Select Food Items</label>
          <select
            name="foodItems"
            multiple
            value={formData.foodItems}
            onChange={handleFoodSelection}
            required={formData.reservationType === 'delivery'}
          >
            {availableFoodItems.map((item) => (
              <option key={item.id} value={item.name}>
                {item.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Special Instructions</label>
          <textarea
            name="instructions"
            value={formData.instructions}
            onChange={handleChange}
          ></textarea>
        </div>

        <button className="submit-button" onClick={handleSubmit}>
          Submit Reservation
        </button>
      </div>
    </div>
  );
};

export default MakeDelivery;
